import React, { useEffect, useState } from "react";
import "./style.css";

const Temp = () => {
    const [searchValue,setSearchValue] = useState('Bhubaneswar');
    const [tempInfo,settempInfo] = useState({});
    const apikey= process.env.REACT_APP_WEATHER_API;
    const getWeatherInfo = async () =>{
        try{
            let url= `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=${apikey}`;
            let response = await fetch(url);
            let data = await response.json();
            const {temp,humidity,pressure} = data.main;
            const {main:wearthermood} =data.weather[0];
            const {name} = data;
            const {speed} = data.wind;
            const {country ,sunset} = data.sys;

            const myNewWeatherInfo = {
              name,temp,humidity,pressure,wearthermood,speed,country,sunset
            };
            settempInfo(myNewWeatherInfo);
        }
        catch(error){
            console.log(error);
        }
    };
    useEffect(()=>{getWeatherInfo()},[]);
  return (
    <>
      <div className="wrap">
        <div className="search">
          <input
            type="search"
            placeholder="Search...."
            autoFocus
            id="search"
            className="searchItem"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button className="searchButton" type="button" onClick={getWeatherInfo}>
            Search
          </button>
        </div>
      </div>

      <article className="widget">
        <div className="weatherIcon">
          <i className={"wi wi-day-sunny"}></i>
        </div>
        <div className="weatherInfo">
          <div className="temperature">
            <span>26.3&deg;</span>
          </div>
          <div className="description">
            <div className="weatherCondition">sunny</div>
            <div className="place">Bhubaneswar,Odisha,India</div>
          </div>
        </div>
        <div className="date">{new Date().toLocaleString()}</div>

        {/* our four part */}
        <div className="extra-temp">
          <div className="temp-info-minmax">
            <div className="two-sided-section">
              <p>
                <i className={"wi wi-sunset"}></i>
              </p>
              <p className="extra-info-leftside">
                19:25 PM <br />
                Sunset
              </p>
            </div>
            <div className="two-sided-section">
              <p>
                <i className={"wi wi-humidity"}></i>
              </p>
              <p className="extra-info-leftside">
                19:25 PM <br />
                Humidity
              </p>
            </div>
          </div>
          <div className="weather-extra-info">
            <div className="two-sided-section">
                <p>
                  <i className={"wi wi-rain"}></i>
                </p>
                <p className="extra-info-leftside">
                  19:25 PM <br />
                  Pressure
                </p>
            </div>
            <div className="two-sided-section">
              <p>
                <i className={"wi wi-strong-wind"}></i>
              </p>
              <p className="extra-info-leftside">
                19:25 PM <br />
                Speed
              </p>
            </div>
          </div>
        </div>
      </article>
    </>
  );
};

export default Temp;
